    setContacts(newContacts);
  };

  return <div className="app-container">
    <table>
      <thead>
        <tr>
          <th>Liquid Name</th>
          <th>Volume per Multiplier</th>
          <th>Multiplier</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>Ethanol</td>
          <td>500</td>
          <td>2</td>
        </tr>
      </tbody>
    </table>
      <h2>Add a Liquid</h2>
        <form>
          <input
            type="text"
            name="liquid_name"
            required="required"
            placeholder="Liquid name"
            onChange={handleAddFormChange}
          />
          <input
            type="number"
            name="vol_per_multiplier"
            required="required"
            placeholder="Multiplier"
            onChange={handleAddFormChange}
          />
          <input
            type="text"
            name="multiplier"
            required="required"
            placeholder="`Single` or `Multi`"
            onChange={handleAddFormChange}
          />
          <button type="Add liquid to table">Add</button>

        </form>
    </div>;
}

export default App;
