class AssignLabware {
  constructor(vol_per_multiplier, multiplier, single_or_multi){
    this.vol_per_multiplier = vol_per_multiplier;
    this.multiplier = multiplier;
    this.single_or_multi = single_or_multi;
  }
}
// console.log(liquid_vol)
var liquid1 = new AssignLabware(5, 96, 'Multi');


function find_labware(liquid){
  let vol = liquid.vol_per_multiplier*liquid.multiplier;
  let single_or_multi = liquid.single_or_multi;
  let vol_string = 'Total Volume: ' + vol/1000 + 'mL  /// '

  if (liquid.single_or_multi == 'Multi') {   // MULTI-CHANNEL ACCESS
    if (vol > 4000 && vol < 90000){
      let num_wells = Math.ceil(vol/14000);
      return(vol_string +
      ' Recommended labware: ' + ' Nest 12 Well Reservoir /// ' + num_wells + ' wells');
    }
    else if (vol > 50000){
      let labware_count = Math.ceil(vol/185000);
      return('Total Volume: ' + vol/1000 + 'mL  /// ' +
      ' Recommended labware: ' + labware_count + ' Nest 1 Well Reservoir(s)');
    }
    else if (vol < 4000) {
      return('Total Volume: ' + vol/1000 + 'mL  /// ' +
            ' Recommended labware: ' + 'Well plate');
    }
  }

  if (liquid.single_or_multi == 'Single'){               // SINGLE-CHANNEL ACCESS
    if (vol > 8000 && vol < 90000){
      let num_well_res = Math.ceil(vol/14000);
      let num_tubes_falcon = Math.ceil(vol/46000);
      let num_tubes_15mL = Math.ceil(vol/12000);
      return('Nest 12 Well Reservoir with ' + num_well_res + ' well(s), ' +
             'Falcon 50mL tubes with ' + num_tubes_falcon + ' tube(s), ' +
             'or 15mL tubes with ' + num_tubes_15mL + ' tube(s)'
    );
    }
    else if (vol < 8000){
      let num_tubes_1500ul = Math.ceil(vol/1400);
      let num_tubes_2mL = Math.ceil(vol/1800);
      return('(' + num_tubes_1500ul + ')' + ' 1.5mL tube(s)' +
             ' or ' + '(' + num_tubes_2mL + ')' + ' 2mL tube(s)');
    }
  }
}
console.log(find_labware(liquid1));

module.exports = {find_labware}
